<script>
    function showAllSubcategories(categorySlug) {
        const showMoreBtn = document.getElementById(`show-more-${categorySlug}`);
        const hiddenItems = document.querySelectorAll(`[data-category="${categorySlug}"].hidden`);

        if (showMoreBtn) {
            // Fade out the button
            showMoreBtn.style.transition = 'opacity 0.2s ease';
            showMoreBtn.style.opacity = '0';

            setTimeout(() => {
                showMoreBtn.style.display = 'none';
            }, 200);
        }

        // Show hidden items with staggered animation
        hiddenItems.forEach((item, index) => {
            setTimeout(() => {
                item.classList.remove('hidden');
                item.style.opacity = '0';
                item.style.transform = 'translateY(20px)';

                // Animate in
                requestAnimationFrame(() => {
                    item.style.transition = 'opacity 0.4s ease, transform 0.4s ease';
                    item.style.opacity = '1';
                    item.style.transform = 'translateY(0)';
                });
            }, index * 100); // Stagger the animation by 100ms for each item
        });
    }

    document.addEventListener('DOMContentLoaded', function() {
        const scroller = document.getElementById('categoryScroller');
        const scrollLeftBtn = document.getElementById('scrollLeft');
        const scrollRightBtn = document.getElementById('scrollRight');

        // Check if scrolling is needed and show/hide arrows accordingly
        function updateArrowVisibility() {
            const isScrollable = scroller.scrollWidth > scroller.clientWidth;
            const isAtStart = scroller.scrollLeft <= 10; // Small buffer for precision
            const isAtEnd = scroller.scrollLeft >= scroller.scrollWidth - scroller.clientWidth - 10;

            if (isScrollable) {
                scrollLeftBtn.classList.toggle('hidden', isAtStart);
                scrollRightBtn.classList.toggle('hidden', isAtEnd);
            } else {
                scrollLeftBtn.classList.add('hidden');
                scrollRightBtn.classList.add('hidden');
            }
        }

        // Scroll left
        scrollLeftBtn.addEventListener('click', () => {
            scroller.scrollBy({
                left: -220, // Slightly more than category width (110px)
                behavior: 'smooth'
            });
        });

        // Scroll right
        scrollRightBtn.addEventListener('click', () => {
            scroller.scrollBy({
                left: 220,
                behavior: 'smooth'
            });
        });

        // Update arrow visibility on scroll
        scroller.addEventListener('scroll', updateArrowVisibility);

        // Update arrow visibility on window resize
        window.addEventListener('resize', updateArrowVisibility);

        // Initial check after a small delay to ensure everything is rendered
        setTimeout(updateArrowVisibility, 100);
    });
</script>
<style>
    .scrollbar-hide {
        -ms-overflow-style: none;
        scrollbar-width: none;
    }
    .scrollbar-hide::-webkit-scrollbar {
        display: none;
    }
</style>
